window.kentico.pageBuilder.registerInlineEditor("color-editor",{init:function(e){var t=e.editor;t.querySelector("#modal-btn").addEventListener("click",(function(){kentico.modalDialog.open({url:t.getAttribute("data-url"),applyCallback:function(r){var n=new CustomEvent("updateProperty",{detail:{value:r.document.querySelector('input[name="color"]:checked').value,name:e.propertyName}});t.dispatchEvent(n)},applyButtonText:"Confirm color selection",title:"Select a color",data:{value:e.propertyValue}})}))}}),window.kentico.pageBuilder.registerInlineEditor("number-editor",{init:function(e){var t=e.editor;t.querySelector("#plus-btn").addEventListener("click",(function(){var r=new CustomEvent("updateProperty",{detail:{value:e.propertyValue+1,name:e.propertyName}});t.dispatchEvent(r)})),t.querySelector("#minus-btn").addEventListener("click",(function(){var r=new CustomEvent("updateProperty",{detail:{value:e.propertyValue-1,name:e.propertyName}});t.dispatchEvent(r)}))}});